void Sound_Init(void){
// write this
    DAC5_Init();
    SysTick_IntArm(1, 0);
  
}
extern "C" void SysTick_Handler(void);
void SysTick_Handler(void){ // called at 11 kHz
  // output one value to DAC if a sound is active
    if (index > 0){
        index = index-1;
        DAC5_Out(*arrayPointer);
        arrayPointer++;
    }
    else{
        SysTick->CTRL = 0;
        SysTick->LOAD = 0;
    }
   
}

//******* Sound_Start ************
// This function does not output to the DAC. 
// Rather, it sets a pointer and counter, and then enables the SysTick interrupt.
// It starts the sound, and the SysTick ISR does the output
// feel free to change the parameters
// Sound should play once and stop
// Input: pt is a pointer to an array of DAC outputs
//        count is the length of the array
// Output: none
// special cases: as you wish to implement
void Sound_Start(uint32_t period){
// write this
    if (kickFlag == 1){
        arrayPointer = kick;
        index = 3971;
        SysTick->VAL = 7;
        SysTick->CTRL = 0x07;
        SysTick->LOAD = period;
        kickFlag = 0;
    }
    else if (punchFlag == 1){
        arrayPointer = punchreal;
        index = 3867;
        SysTick->VAL = 7;
        SysTick->CTRL = 0x07;
        SysTick->LOAD = period;
        punchFlag = 0;
    }
    else if (shieldFlag == 1){
        arrayPointer = shieldreal;
        index = 3971;
        SysTick->VAL = 7;
        SysTick->CTRL = 0x07;
        SysTick->LOAD = period;
        shieldFlag = 0;
    }
    else{
        return;
    }
}
void Sound_Punch(void){
    punchFlag = 1;
    Sound_Start(7273);
// write this
  
}
void Sound_Kick(void){
    kickFlag = 1;
    Sound_Start(7273);
// write this
  
}

void Sound_Shield(void){
    shieldFlag = 1;
    Sound_Start(7273);
}
